import React, { useState } from "react";

const LINKS = [
  { id: "home", label: "Home" },
  { id: "about", label: "About" },
  { id: "projects", label: "Projects" },

  { id: "contact", label: "Contact" },
];

export default function Navbar() {
  const [open, setOpen] = useState(false);

  const handleNav = (e, id) => {
    e.preventDefault();
    setOpen(false);
    document.getElementById(id)?.scrollIntoView({ behavior: "smooth", block: "start" });
  };

  return (
    <header className="fixed w-full z-50">
      <div className="bg-black/60 backdrop-blur-sm border-b border-zinc-800">
        <div className="container mx-auto px-6 py-4 flex items-center justify-between">
          <a href="#home" onClick={(e) => handleNav(e, "home")} className="text-2xl font-display font-semibold text-white">
            <span className="text-orangeGlow">A</span>njani
          </a>

          <nav className="hidden md:flex items-center gap-8">
            {LINKS.map((l) => (
              <a
                key={l.id}
                href={`#${l.id}`}
                onClick={(e) => handleNav(e, l.id)}
                className="text-sm text-white/80 hover:text-white transition"
              >
                {l.label}
              </a>
            ))}
          </nav>

          {/* Mobile controls */}
          <div className="md:hidden flex items-center gap-3">
            <button aria-label="Toggle menu" onClick={() => setOpen((s) => !s)} className="p-2">
              <div className="w-6 h-0.5 bg-white mb-1"></div>
              <div className="w-6 h-0.5 bg-white mb-1"></div>
              <div className="w-6 h-0.5 bg-white"></div>
            </button>
          </div>
        </div>

        {open && (
          <div className="md:hidden bg-black/95 border-t border-zinc-800">
            <div className="flex flex-col px-6 py-4 gap-3">
              {LINKS.map((l) => (
                <a
                  key={l.id}
                  href={`#${l.id}`}
                  onClick={(e) => handleNav(e, l.id)}
                  className="text-white/90 py-2"
                >
                  {l.label}
                </a>
              ))}
            </div>
          </div>
        )}
      </div>
    </header>
  );
}
