import React, { useState } from 'react';

export default function ContactForm() {
    const [formData, setFormData] = useState({ name: '', email: '', message: '' });
    const [status, setStatus] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((s) => ({ ...s, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // simulate submit
        setStatus('Message sent — thank you!');
        setTimeout(() => setStatus(''), 4000);
        setFormData({ name: '', email: '', message: '' });
    };

    return (
        <section id="contact" className="py-20 bg-[#030417] text-white">
            <div className="max-w-4xl mx-auto px-6">
                <p className="text-sm uppercase tracking-widest text-[var(--accent)] text-center">Get in touch</p>
                <h2 className="text-4xl sm:text-5xl font-display font-bold text-center mt-3">Let's Work Together</h2>
                <p className="text-center text-gray-400 mt-3"> Drop me a message and let's create something amazing.</p>

                <form onSubmit={handleSubmit} className="mt-10 bg-[#0b1220] rounded-2xl p-6 md:p-10 shadow-lg border border-white/6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label htmlFor="name" className="text-sm font-medium text-gray-300">Your Name</label>
                            <input
                                id="name"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                                placeholder="John Doe"
                                className="mt-2 w-full rounded-xl bg-[#0e1624] border border-white/6 px-4 py-3 placeholder:text-gray-400 text-white focus:outline-none focus:ring-2 focus:ring-[var(--accent)]"
                            />
                        </div>

                        <div>
                            <label htmlFor="email" className="text-sm font-medium text-gray-300">Your Email</label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                placeholder="john@example.com"
                                className="mt-2 w-full rounded-xl bg-[#0e1624] border border-white/6 px-4 py-3 placeholder:text-gray-400 text-white focus:outline-none focus:ring-2 focus:ring-[var(--accent)]"
                            />
                        </div>
                    </div>

                    <div className="mt-6">
                        <label htmlFor="message" className="text-sm font-medium text-gray-300">Your Message</label>
                        <textarea
                            id="message"
                            name="message"
                            value={formData.message}
                            onChange={handleChange}
                            required
                            placeholder="Tell me about your project..."
                            rows="8"
                            className="mt-2 w-full rounded-xl bg-[#0e1624] border border-white/6 px-4 py-4 placeholder:text-gray-400 text-white focus:outline-none focus:ring-2 focus:ring-[var(--accent)]"
                        />
                    </div>

                    <button
                        type="submit"
                        className="mt-6 w-full py-4 rounded-xl text-white font-semibold bg-gradient-to-r from-[var(--primary)] to-[var(--accent)] shadow-glow hover:opacity-95 transition"
                    >
                        Send Message
                    </button>

                    {status && <p className="mt-4 text-sm text-green-400 text-center">{status}</p>}
                </form>

                <div className="mt-6 text-center">
                  <p className="text-sm text-gray-400">
                    Or reach me directly at{" "}
                    <a
                      href="mailto:uthumwijenayake@gmail.com"
                      className="font-medium bg-gradient-to-r from-[var(--primary)] to-[var(--accent)] bg-clip-text text-transparent hover:underline"
                    >
                      uthumwijenayake@gmail.com
                    </a>
                  </p>
                </div>
            </div>
        </section>
    );
}
