import React, { useState } from 'react';

const ContactForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here (e.g., send data to an API)
        console.log('Form submitted:', formData);
        setFormData({ name: '', email: '', message: '' }); // Reset form
    };

    return (
        <section className="container mx-auto px-6 py-12">
            <div className="max-w-2xl mx-auto bg-white dark:bg-card rounded-xl p-6 md:p-8">
                <h3 className="text-xl font-semibold mb-4">Contact Me</h3>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        required
                        className="w-full bg-transparent border border-zinc-700 rounded px-3 py-2 input-focus"
                        placeholder="Full name"
                    />
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        className="w-full bg-transparent border border-zinc-700 rounded px-3 py-2 input-focus"
                        placeholder="Email"
                    />
                    <input
                        type="text"
                        id="subject"
                        name="subject"
                        value={formData.subject}
                        onChange={handleChange}
                        className="w-full bg-transparent border border-zinc-700 rounded px-3 py-2 input-focus"
                        placeholder="Subject"
                    />
                    <textarea
                        id="message"
                        name="message"
                        value={formData.message}
                        onChange={handleChange}
                        required
                        className="w-full bg-transparent border border-zinc-700 rounded px-3 py-2 input-focus"
                        rows="6"
                        placeholder="Your message"
                    />
                    <div className="flex items-center gap-4">
                        <button type="submit" className="bg-orangeGlow text-black px-5 py-2 rounded-md">
                            Send Message
                        </button>
                        <div className="text-sm text-muted">
                            Or email me at{' '}
                            <a className="text-orangeGlow" href="mailto:hello@example.com">
                                hello@example.com
                            </a>
                        </div>
                    </div>
                </form>
            </div>
        </section>
    );
};

export default ContactForm;
