import React from 'react';
import profile from '../assets/images/profile.jpg';

const HeroSection = () => {
    return (
        <section className="container mx-auto px-6 py-12">
            <div className="flex flex-col-reverse lg:flex-row items-center gap-10">
                <div className="flex-1">
                    <h1 className="text-3xl sm:text-4xl lg:text-5xl font-display font-semibold mb-3">
                        Hi, I'm <span className="text-orangeGlow">Samantha Smith</span>
                    </h1>
                    <p className="text-muted dark:text-zinc-400 max-w-2xl mb-6">
                        Full-stack Web Developer â€” building high quality, accessible, and delightful user interfaces.
                    </p>
                    <div className="flex gap-4">
                        <a href="#projects" className="bg-orangeGlow text-black px-5 py-2 rounded-md shadow-glow hover:scale-[1.02] transition">See Projects</a>
                        <a href="#contact" className="border border-orangeGlow text-orangeGlow px-5 py-2 rounded-md hover:bg-orangeGlow/10">Contact</a>
                    </div>
                </div>

                <div className="w-40 h-40 sm:w-48 sm:h-48 lg:w-56 lg:h-56 relative">
                    <div className="absolute inset-0 rounded-full glow" style={{ background: 'radial-gradient(ellipse at center, rgba(255,106,0,0.12), transparent 40%)' }} />
                    <img src={profile} alt="profile" className="relative rounded-full w-full h-full object-cover border-2 border-transparent" />
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
