import React from 'react';
import profile from '../assets/images/profile.jpg';

export default function About() {
  return (
    <section className="container mx-auto px-6 py-10">
      <div className="max-w-3xl mx-auto bg-white dark:bg-card rounded-xl p-6 md:p-10 shadow-glow">
        <div className="flex flex-col items-center gap-4 text-center">
          <div className="w-28 h-28 rounded-full overflow-hidden ring-4 ring-orangeGlow">
            <img src={profile} alt="me" className="w-full h-full object-cover" />
          </div>
          <h2 className="text-2xl font-semibold">About Me</h2>
          <p className="text-muted dark:text-zinc-400">
            I'm a full-stack developer building performant web applications with React, Tailwind CSS and Node.
            I enjoy creating meaningful experiences and shipping polished products.
          </p>
        </div>
      </div>
    </section>
  );
}
